targets.query:
from(bucket: "${bucket}")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) =>
    r.host == "${device}" and
    r._field == "reboots"
  )
  |> aggregateWindow(
  every: 1d,
  fn: count)
  |> keep(columns:["_field", "_time", "_value"])

